3681.source:
function analyze() {

    this.tables = {}
    var morphs = []
    this.morphs  = morphs;

    this.resetLog()


    Properties.ownValues(this.worlds).each(function(worldItem) {
        var world = worldItem.object
        if (!world || !world.withAllSubmorphsDo) return;
        try {
            var morphsPerWorld = []
            world.withAllSubmorphsDo(function(eaMorph) {
                morphsPerWorld.push({object: eaMorph, url: worldItem.url})
            })
            worldItem.morphs = morphsPerWorld
            this.morphs.pushAll(morphsPerWorld)
        } catch(e)  {
            this.log("Error: " + e)
        }
    }, this)

    Properties.ownValues(this.parts).each(function(partItem) {
        var part = partItem.object
        if (!part || !part.withAllSubmorphsDo) return;
        try {
            var morphsPerPart = []
            part.withAllSubmorphsDo(function(eaMorph) {
                morphsPerPart.push({object: eaMorph, url: partItem.url})
            })
            partItem.morphs = morphsPerPart
            this.morphs.pushAll(morphsPerPart)
        } catch(e)  {
            this.log("Error: " + e)
        }
    }, this)
    this.tables["Stats"] = [[ "Key", "Value"],
        ["loadable worlds", Properties.values(this.worlds).length],
        ["morphs", this.morphs.length]]

    this.analyzeWorlds()
    this.analyzeMorphs()
    this.analyzeParts()
    this.analyzeLoading()
    this.update()
}

