3737.source:
function loadURL(url) {

    url = new URL(url)
    var obj
    var startTime = Date.now()
    if (url.filename().match(".xhtml")) {
        var xml = new WebResource(url).get().contentDocument;
        var json = $(xml).find("#LivelyJSONWorld").text();
        try {
            obj = this.deserializeObjectFromJSON(json)
        } catch(e) {
            this.urls[url].error = e
        }
        this.worlds[url] = {object: obj, url: url, isWorld: true}
    } else if (url.filename().match(".json")) {
        var json = new WebResource(url).get().content;
        try {
            obj = this.deserializeObjectFromJSON(json)
        } catch(e) {
            this.urls[url].error = e
        }
        this.parts[url] = {object: obj, url: url, isPart: true}
    }
    this.urls[url].loaded = true;
    this.urls[url].loadTime = Date.now() - startTime;
    this.urls[url].warnings = RobustDeserializionLayer.lastWarnings;
    RobustDeserializionLayer.lastWarnings = []
    // this.updateLoadingTable()
    return obj
}

