function deserializeObjectFromJSON(json) {
     var jso = JSON.parse(json);
    var world;
    cop.withLayers([RobustDeserializionLayer], function() {
        var serializer = lively.persistence.Serializer.createObjectGraphLinearizer();
        serializer.plugins = serializer.plugins.reject(function(ea) {
            return ea instanceof StoreAndRestorePlugin })
        world = serializer.deserializeJso(jso);

        // world = lively.persistence.Serializer.deserializeWorldFromJso(jso)

    })
    return world
}

