function analyzeWorlds() {
        this.tables["Worlds"] = [[ "Path", "Morphs"]].pushAll(
        Properties.ownValues(this.worlds).collect(function(eaItem) {
            return [new URL(eaItem.url).relativePathFrom(URL.root),
                eaItem.morphs && eaItem.morphs.length]
        })
    )

}
