275.source:
function updateDates() {
    if (!this.timeStamps) this.timeStamps = {}

    var last = this.timeStamps["last"];
    if (!last || last.words != this.textString.length) {
        this.timeStamps["last"] = {words: this.textString.length, date: new Date()};
        this.showDates()
    }
}

283.source:
function showDates() {

    if (!this.dateLabel || !this.dateLabel.owner) {
       this.dateLabel = new lively.morphic.Text(new Rectangle(-40,0,20,10), "").beLabel({
                fill: Color.white,
                fontSize: 8,
                align: 'center',
                fixedWidth: false,
                fixedHeight: false,
                opacity: 0.8,
                borderRadius: 2,
                borderWidth: 0,
                borderColor: Color.black,
                padding: Rectangle.inset(5,2, 5,0)
            });
        this.addMorph(this.dateLabel)
    }
    var last = this.timeStamps["last"];
    if (last) 
        this.dateLabel.textString = last.date.format("HH:MM")
}

