function setTarget(aLayouter) {
    this.setTargetName(aLayouter ? lively.Class.getConstructor(aLayouter).name : '');
    this.target = aLayouter;
    if (aLayouter) {
        this.get('borderWidthSlider').setValue(aLayouter.getBorderSize());
        this.get('spacingSlider').setValue(aLayouter.getSpacing());
    }
    }

