function setupConnections() {
    connect(this.get('borderWidthSlider'), 'value',
        this, 'setBorderSize', {converter: function(v) {return v;}});
    connect(this.get('spacingSlider'), 'value',
        this, 'setSpacing', {converter: function(v) {return v;}});

}
