function updateRichTextDiffs(textMorph, diffs) {
	var string = "";
	for (var x = 0; x < diffs.length; x++) {
		string += diffs[x][1];
	 }
	textMorph.setTextString(string);

	var from=0;
	for (var x = 0; x < diffs.length; x++) {
		var op = diffs[x][0];    // Operation (insert, delete, equal)
		var data = diffs[x][1];  // Text of change.
		var to =  from + data.length;
		switch (op) {
			case DIFF_INSERT:
			textMorph.emphasize({color: 'green', textDecoration: 'underline'}, from, to)
			break;
			case DIFF_DELETE:
			textMorph.emphasize({color: 'red', textDecoration: 'line-through'}, from, to)
	        	break;
			case DIFF_EQUAL:
	        	break;
	    	}
		from = to
	 }
}

