function updatePatchText( diffs) {
	var dmp = new diff_match_patch()
	var patches = dmp.patch_make(diffs)

	this.get('TextPatch').setTextString(JSON.serialize(patches))

	var patchText = dmp.patch_toText(patches);

	this.get('TextPrettyPatch').setTextString(patchText)
}
