function onFire() {

        var journal = this.get('Journal');

        var today = journal.submorphs.sortBy(function(ea) { 
                return ea.getPosition().y })[1];

	var words = 0;
        today.submorphs.each(function(ea) {
            if (ea.textString)
                words += ea.textString.split(/\s+/).length;
        })

	var counterText = this.get('wordCounterText')
	counterText.setTextString(
		new Date() + ': \t' + words+ '\n' + counterText.textString 
	)
}

