function updateWords() {
        var lines = Strings.lines(this.textString)
        data = lines.collect(function(line) {
            var date = new Date(line.split('\t')[0]),
                count = Number(line.split('\t')[1]);
            return {count: count, date: date};
        }).select(function(ea) { return ea && ea.count }).reverse()

	this.get('WordTotalDiagram').setData(data)

        var today = new Date().format("yymmdd");
	this.get('WordSpeedDiagram').setData( 
            data.select(function(ea) {
                return ea.date.format("yymmdd") == today
        }))
}

