function draw(w, h) {
	w = w - 40;
	h = h - 35;
	if (!Global.pv) return ;

	var vis = new pv.Panel()
		.width(w)
		.height(h)
		.bottom(30)
		.left(30)
		.right(10)
		.top(5);

	var data = this.data, // {date: Date, count: Number}
                xMin = data.first().date,
		xMax = data.last().date,
                x = function(d) { return (d.date -xMin) / (xMax-xMin) * w },
    		yVals = data.pluck('count'),
                yMax = pv.max(yVals) + 200,
		y = pv.Scale.linear(
                    pv.min(yVals.concat(0)),
                    pv.max(yVals)).range(0, h);

	vis.add(pv.Rule)
		.data(y.ticks())
		.bottom(y)
		.strokeStyle(function(d) { return d ? "#eee" : "#000"; })
	.anchor("left").add(pv.Label)
		.text(this.yFormat || y.tickFormat);

        var timeFrame = data.last().date - data[0].date,
            timeStep = timeFrame / 5,
            middleDates = Array.range(1,4).collect(function(i) {
                return {date: new Date(data[0].date.getTime() + timeStep*i)}
            }).reverse(),
            xText = [data[0]].concat(middleDates).concat(data.last());
        // , data[Math.round(data.length / 2)], data.last()
        // var xText = [data[0], data[Math.round(data.length / 2)], data.last()];
	vis.add(pv.Rule)
		.data(xText)
		// .visible(function(d) { return d != 0; })
		.left(x)
		.bottom(-5)
		.height(5)
	.anchor("bottom").add(pv.Label)
            .textBaseline("top")
        .textAngle(Math.PI / 9)

	    .text(this.xFormat || x.tickFormat);

	vis.add(pv.Area)
		.def('idx', 0)
		.data(data)
		.bottom(1)
		.left(function(d) {
			return x(d);
		})
		.height(function(d) {
                    return d.count / yMax * h
		})
		.fillStyle("rgb(121,173,210)")
	.anchor("top").add(pv.Line)
		.lineWidth(3);

	return vis;
}

