951.source:
function gatherSlides() {

    var slides = [];
    this.world().submorphs.each(function(ea) {
        if(ea instanceof lively.Presentation.PageMorph) {
            slides.push(ea);
            ea.lastWorldPosition = ea.getPosition()
        }
        if(ea.slide && ea.slide instanceof lively.Presentation.PageMorph) {
            ea.slide.lastWorldPosition = ea.getPosition()

            ea.submorphs.invoke('remove')
            var thumb = this.thumbForSlide(ea.slide)
            ea.addMorph(thumb);
            slides.push(ea.slide);
        }

    }, this)

    return slides.uniq().sort(function(a, b) { 
        var posA = a.lastWorldPosition || a.getPosition();
        var posB = b.lastWorldPosition || b.getPosition();


        if (Math.abs(posA.y - posB.y) < 20) {
            return posA.x - posB.x 
        }
        return posA.y - posB.y 
    })
}

