function dropSlides() {
    var m = this.get('Container');
    var w = this.world();
    var pos = this.getDropStartPosition()

    m.submorphs.clone()
        .sortBy(function(ea) { return ea.getPosition().y })
        .forEach(function(ea){
        var slide  = ea.slide;
        w.addMorph(slide);
        slide.setPosition(pos)
        pos = pos.addPt(pt(0, 20 + ea.getExtent().y))
        slide.setScale(1);
        slide.enableEvents();
        slide.unignoreEvents()
        ea.remove();
    })
    m.setExtent(m.owner.getExtent());
    m.setScale(1)
}

