function makeItemFor(slide) {
    var item = Morph.makeRectangle(slide.getBounds().insetBy(-10));
    item.isSlideContainer = true;
    // item.disableEvents();
    // slide.disableEvents();
    // slide.ignoreEvents();
    item.slide = slide;
    item.setBorderWidth(10);
    var thumb = this.thumbForSlide(slide)
    thumb.disableEvents();
    thumb.setPosition(pt(10,10))
    thumb.setScale(1)

    item.addMorph(thumb);
    item.setBorderColor(null);
    item.pane = this;
    item.disableDropping()

    item.addScript(function copy() {
        var result = $super();
        // keep that invariant....
        result.slide = result.submorphs.first();
        var title = result.get('title');
        if (title && title.textString) title.textString += ' Copy';
        result.setBorderColor(Color.gray)
        return result
    })

    item.addScript(function onDropOn(morph) {
        if (morph.name == 'Container') {
            morph.get('PresentationContainerPane').onAddSlideItem(this)
        }
    })
    item.name = 'PresentationItem for ' + slide.name
    item.addScript(function onMouseUp(evt) { 
        if (!this.pane) return;
        return this.pane.onItemMouseUp(this, evt)})
    item.addScript(function onDragStart(evt) { 
        if (!this.pane) {
            evt.hand.grabMorph(this)
            return;
        };
        return this.pane.onItemDragStart(this, evt)})
    item.addScript(function onDragEnd(evt) { 
        if (!this.pane) return;
        return this.pane.onItemDragEnd(this, evt)})
    item.addScript(function showItem() { 
        this.addMorph(this.slide);
        this.slide.disableEvents();
        this.slide.setPosition(pt(10,10))
        this.slide.setScale(1)
        this.slide
    })
    item.setFill(null)
    return item
}

