215.source:
function visitSlide(slideMorph) {
    var no = this.getSlideNo();
    if (no < 1) return;

    slideMorph.addMorphBack(this);
    this.setPosition(pt(0,0))
    this.get('slideNoText').setTextString(no);

    var title = slideMorph.get('title');
    title && title.setBounds(new Rectangle(0,90, title.getExtent().x, 140))

    var content = slideMorph.get('content');
    content && content.setPosition(pt(122,178))
}

