292.source:
function doSnap() {
    var items = $world.submorphs.select(function(ea) { return ea.isSlideContainer} )
    this.snap = pt(350,300)

    items.forEach(function(ea) {
        var p = ea.getPosition();
        p.x += this.snap.x / 2
        p.y += this.snap.y / 2
        p.x = p.x - (p.x % this.snap.x) + 20
        p.y = p.y - (p.y % this.snap.y) + 20
        ea.setPosition(p)
    }, this)
}
