function projectSlide(slide) {
    if (this.targetSlide && (this.targetSlide.owner === this)) this.targetSlide.remove();
//    this.submorphs.select(function(ea) {
//        return  ea instanceof lively.Presentation.PageMorph
//    }).invoke('remove')

    this.addMorph(slide)
    this.targetSlide = slide;
    slide.setScale(1)
    this.setExtent(slide.getExtent().addPt(pt(20, 20)))
    slide.setPosition(pt(10,10))
    slide.enableEvents();
    slide.unignoreEvents();
    slide.enableDropping()
}

