function loadIFrame(mode) {
    this.removeIFrame();
    var url = 'http://stefanwehrmeyer.com/projects/bloomfilter/';
    if (mode === 'simple'){
        url += 'simple.html';
    }
    var iFrame = this.iFrame = lively.morphic.World.loadInIFrame(url);
    this.addMorph(iFrame);
    iFrame.setPosition(pt(0, 30));
    iFrame.setExtent(pt(900, 800));
    // this.setExtent(iFrame.bounds().extent().addXY(5, iFrame.getPosition().y + 50));
    this.setExtent(pt(1024, 800));
}
