function doAction() {
    var last = this.owner.submorphs.without(this).last(),
        lastCopy = last.copy();
    this.owner.addMorph(lastCopy);
    lastCopy.align(last.bounds().bottomLeft(), lastCopy.bounds().topLeft());
    if (lastCopy.isText) {
        lastCopy.insertTextStringAt(lastCopy.textString.length , " ...");
    }
    this.owner.applyLayout();
}

