function initHTML(ctx) {
        if (!ctx.morphNode) ctx.morphNode = XHTMLNS.create('ul');;
        this.setFocusableHTML(ctx, this.isFocusable());
        //this.setPivotPointHTML(ctx, this.getPivotPoint())
