function onFire() {
    var backup = lively.PartsBin.getPart('SandboxBackup', 'PartsBin/Sandbox');
    if (!backup) {
        alert('reset not possible');
        return;
    }
    this.world().removeAllMorphs();
    backup.submorphs.forEach(function(ea) {
        $world.addMorph(ea);
    })
    backup.remove();
}

