231.source:
function select(selector) {
    var siz = new lively.morphic.Sizzle(),
        newSelectedMorphs = [];
    this.deselectAll();
    if (selector.length > 0) {
        var selectedMorphs;
        try {
            selectedMorphs = siz.select(selector);
        } catch(e) {
            selectedMorphs = [];
        }
        selectedMorphs.each(function(m) {
                var shapeNode = m.renderContext().shapeNode;
                if (shapeNode) {
                    $(shapeNode).css('outline', '10px solid red');
                    newSelectedMorphs.push(m);
                }
            });

    }
    this.selectedMorphs = newSelectedMorphs;
}

235.source:
function deselect(selectedMorphs) {
    selectedMorphs.each(function(m) {
            var shapeNode = m.renderContext().shapeNode;
            if (shapeNode) {
                $(shapeNode).css('outline', '');
            }
        })
}

