function drawSeries(contexts, series, scales) {
    var scale = scales.first(),
        x = scale.x,
        y = scale.y;
    if (x && y) {
        var stack = d3.layout.stack()
                .values(function(d) { return d.values; })
                .x(function(d) { return (isNaN(d.x) || d.x == null) ? 0 : d.x; })
                .y(function(d) { return (isNaN(d.y) || d.y == null) ? 0 : d.y; })
                .out(function(d, y0, y) {
                        d.y0 = y0;
                })
                .order("reverse"),
            area = d3.svg.area()
                .interpolate(this.interpolation || 'basis')
                .tension(this.tension)
                .x(function(d) { return x(d.x); })
                .y0(function(d) { return y(d.y0); })
                .y1(function(d) { return y(d.y0 + d.y); });
        try {
            stack(series);
        } catch(e) {
            alert('Value count has to be equal for all series in the stack');
        }

        var seriesRepresentation = d3.select(context).selectAll("path.area")
                .data(series);
        seriesRepresentation.enter()
            .insert("svg:path");
        seriesRepresentation.exit()
            .remove();

        seriesRepresentation.each(function(d) {
            d3.select(this)
                .datum(d.values)
                .transition().duration(10)
                .attr("d", area)
                .attr("class", 'area '+ (d.className ? ' '+ d.className : ''));
        });

    } else {
        console.warn('Cannot draw lines when no scales are defined');
    }
}
