function insertPropertyField(decl, allDeclarations) {
    /*
    Creates a property editing field in the style browser.

    Property (String): The name of the CSS property to edit
    Values (Array): The property's values the panel is initialized with
    Active (Bool): Is the property active for the inspected morph?
    */
    var property = decl.property,
        values = decl.values.collect(
            function(value){
                return value.value;
            }),
        overriddenBy = decl.overriddenBy,
        backPanel = this.get('PropertiesPanel'),

        cssProp = lively.morphic.CSSProperties[property.trim().toLowerCase()],
        valueTypes = this.getValueTypes(cssProp, values),
        valueCount = valueTypes.length,

        // the panel that is created
        panelSpacing = 10,
        panelHeight = 50,
        actualPanelHeight = (valueCount === 0)
            ? panelHeight : panelHeight * valueCount,
        panelWidth = backPanel.getExtent().x,
        widgetHeight = 20,
        widgetTop = Math.round(panelHeight / 2 - widgetHeight / 2),
        allDeclExtraHeight = widgetHeight,
        allDeclRuleOriginTop = widgetTop + allDeclExtraHeight + 10,
        leftMargin = 200,
        rightMargin = 65,
        labelMargin = 10,
        panelTop = 0,
        panelStyle = {fill: Color.fromString('#ddd'),
            borderWidth: 0, resizeWidth: true, adjustForNewBounds: true},
        panel = lively.morphic.Morph.makeRectangle(
            0,panelTop, panelWidth, actualPanelHeight),

        // text morph on the panel that shows the prop name
        propertyNameDisplayStyle = {fill: null, fontSize: 10, borderWidth: 0},
        propertyNameDisplay = new lively.morphic.Text(
            new Rectangle(labelMargin, widgetTop, leftMargin,widgetHeight));

    panel.applyStyle(panelStyle );
    panel.declaration = decl;

    propertyNameDisplay.textString = property;
    propertyNameDisplay.applyStyle(propertyNameDisplayStyle);

    // If the "all declarations" option is displayed, 
    // panels also show their origin rules. If the declaration
    // is overridden, the override rule is shown
    if (overriddenBy || allDeclarations) {
        var ruleOriginDisplay = new lively.morphic.Text(
                new Rectangle(labelMargin,
                allDeclRuleOriginTop, panelWidth - labelMargin, widgetHeight)),
            ruleOriginDisplayStyle = {fill: null, fontSize: 8, borderWidth: 0};

        panel.setExtent(panel.getExtent().addXY(0, allDeclExtraHeight));

        ruleOriginDisplay.applyStyle(ruleOriginDisplayStyle);
        ruleOriginDisplay.setName('RuleOriginDisplay');
        panel.addMorph(ruleOriginDisplay);

        if (allDeclarations) {
            ruleOriginDisplay.textString = 'defined in ' + decl.rule.selectorText();
        } else if (overriddenBy) {
            propertyNameDisplay.applyStyle({textDecoration: 'line-through'});
            ruleOriginDisplay.textString = 'overridden by ' + overriddenBy.selectorText();
            panel.applyStyle({/*fill: Color.fromString('#999')*/opacity: 0.7});
        }
    }

    propertyNameDisplay.setName('PropertyNameDisplay');
    panel.addMorph(propertyNameDisplay);

    panel.addMorph(this.createDeleteButton(
        new Rectangle(panelWidth - rightMargin + 2 * labelMargin ,
        widgetTop, widgetHeight, widgetHeight), decl));

    for (var i = 0; i < valueCount; i++) {
        var valuePanel = this.makeValuePanel(
                valueTypes[i], panelWidth - leftMargin - rightMargin, panelHeight);
        panel.addMorph(valuePanel);
        valuePanel.setPosition(pt(leftMargin, i * panelHeight));
        valuePanel.setValue(values[i]);
    }
    if (valueCount === 0) {
        var valuePanel = this.makeValuePanel(
                0, panelWidth - leftMargin - rightMargin, panelHeight);
        panel.addMorph(valuePanel);
        valuePanel.setPosition(pt(leftMargin, 0));
        valuePanel.setValue(decl.valueText);
    }

    backPanel.addMorph(panel);


}

