function makeValuePanel(type, width, height) {
    var panel = lively.morphic.Morph.makeRectangle(0,0, width, height),
        panelStyle = {fill: null, borderWidth: 0},
        resizeWidthStyle = {resizeWidth: true, adjustForNewBounds: true},
        widgetHeight = 20,
        widgetTop = Math.round(height / 2 - widgetHeight / 2);

    switch (type) {
        case 1: // Number
            var slider = this.world().loadPartItem(
                    'Slider', 'PartsBin/Inputs'),
                sliderScale = 64,

                // see http://www.w3.org/TR/css3-values/#lengths
                unitMenuItems = ['px', 'em', 'pt', 'cm', 'mm', 'in', 'pc',
                    'ex', 'ch', 'rem', 'vw', 'vh', 'vmin','vmax'],
                unitMenu = this.world().loadPartItem(
                    'DropDownList', 'PartsBin/Inputs'),
                unitMenuWidth = 60,
                unitMenuStyle = {moveHorizontal: true},

                valueDisplayWidth = 40,
                valueDisplay = new lively.morphic.Text(
                    new Rectangle(0,0, valueDisplayWidth, widgetHeight)),
                valueDisplayStyle = {
                    textSize: 10, fill: null, borderWidth: 0,
                    align: 'center', moveHorizontal: true};

            slider.setExtent(
                pt(width - unitMenuWidth - valueDisplayWidth, widgetHeight));
            unitMenu.setExtent(pt(unitMenuWidth, widgetHeight));

            slider.setPosition(pt(0, widgetTop));
            slider.applyStyle(resizeWidthStyle);
            slider.valueScale = sliderScale;
            slider.setName('Slider');
            unitMenu.applyStyle(unitMenuStyle);
            unitMenu.setPosition(pt(width - unitMenuWidth, widgetTop));
            unitMenu.updateList(unitMenuItems);
            unitMenu.setName('UnitMenu');
            valueDisplay.setPosition(
                pt(width - unitMenuWidth - valueDisplayWidth, widgetTop));
            valueDisplay.applyStyle(valueDisplayStyle);

            connect(slider, 'value', valueDisplay, 'textString', {
                converter: function(val){
                        return Math.round(val);
                    }});

            panel.addMorph(slider);
            panel.addMorph(unitMenu);
            panel.addMorph(valueDisplay);

            panel.addScript(function setValue(value){
                    var slider = this.get('Slider'),
                        unitMenu = this.get('UnitMenu'),
                        valueNumber = parseFloat(value),
                        valueUnit = value.match(
                            /px|em|ex|%|in|cm|mm|pt|pc|ch|rem|vw|vh|vmin|vmax/
                            ).first();
                    slider.setValue(valueNumber || 0);
                    unitMenu.setSelectionMatching(valueUnit || 'px');
                });
            panel.addScript(function updateDeclaration(){

                        var slider = this.get('Slider'),
                            unitMenu = this.get('UnitMenu'),
                            styleBrowser = this.get('StyleBrowser'),
                            valueText = slider.value + unitMenu.selection;
                    if (this.owner && styleBrowser) {
                        this.owner.declaration.values[0].value = valueText;
                        styleBrowser.declarationHasChanged(this.owner.declaration);
                    }
                });
            connect(slider, 'value', panel, 'updateDeclaration');
            connect(unitMenu, 'selection', panel, 'updateDeclaration');

            break;
        case 2: // Option
            // TODO: Drop down box
            break;
        case 3 : // Color
            var colorField = this.world()
                .loadPartItem('ColorPickerButton', 'PartsBin/Inputs');
            colorField.setExtent(pt(width, widgetHeight));
            colorField.setPosition(pt(0, widgetTop));
            colorField.setName('ColorField');
            colorField.applyStyle(resizeWidthStyle);
            panel.addMorph(colorField);
            panel.addScript(function setValue(value){
                    var color = Color.fromString(value),
                        colorField = this.get('ColorField');
                    colorField.setColor(color);
                });
            panel.addScript(function updateDeclaration(){
                        var colorField = this.get('ColorField'),
                            styleBrowser = this.get('StyleBrowser'),
                            valueText = colorField.color.toRGBAString();
                    if (this.owner && styleBrowser) {
                        this.owner.declaration.values[0].value = valueText;
                        styleBrowser.declarationHasChanged(this.owner.declaration);
                    }
                });
            connect(colorField , 'color', panel, 'updateDeclaration');
            break;
        case 4: // Shadow
            // TODO: make shadow dialog
            break;
        default: // text only
            var edit = new lively.morphic.Text(
                    new Rectangle(0,widgetTop, width, widgetHeight)),
                editStyle = {fill: Color.white, borderWidth: 1,
                    borderColor: Color.black, padding: new Rectangle(0,0,0,0)};
            edit.applyStyle(editStyle);
            edit.applyStyle(resizeWidthStyle);
            edit.setName('EditField');
            panel.addMorph(edit);
            panel.addScript(function setValue(value){
                    var edit = this.get('EditField');
                    edit.textString = value;
                });
            panel.addScript(function updateDeclaration(){
                        var edit = this.get('EditField'),
                            styleBrowser = this.get('StyleBrowser'),
                            valueText = edit.textString;
                    if (this.owner && styleBrowser) {
                        this.owner.declaration.valueText = valueText;
                        styleBrowser.declarationHasChanged(this.owner.declaration);
                    }
                });
            connect(edit, 'textString', panel, 'updateDeclaration');
    }

    panel.applyStyle(panelStyle);
    panel.applyStyle(resizeWidthStyle);
    return panel;

}

