function getValueTypes(cssProp, values) {
    var valueCount = values.length,
        valueTypes = [];

    if (cssProp) {
        if (cssProp[valueCount]) {
            var x;
            for (var i = 1; (x = cssProp[valueCount][i]); i++) {
                valueTypes.push(x.type || 0);
            }
        } else {
            var v = valueCount,
                x;
            while (v > 0 && !cssProp[v]) {
                v--;
            }
            if (v === 0) {
                alert('Woops! The property cannot have '+valueCount+' values :(');
                return;
            }
            for (var i = 1; (x = cssProp[v][i]); i++) {
                valueTypes.push(x.type || 0);
            }
        }
        return valueTypes;
    } else {
        return []
    }
}

