function setRules(target) {
    // rules are ordered already, right?
    var dropDownListItems = [],
        dropDownList = this.get('RuleList'),
        rules = target.getMatchingStyleSheetRules(),
        allDeclarations = target.getStyleSheetDeclarations();

    dropDownListItems.push({
            string: '*All active matching declarations*',
            rule: {
                allDeclarations: true,
                declarations: allDeclarations
        }});

    this.rules = this.markOverriddenProperties(rules);
    this.rules.reverse().each(function(rule){
            var morphName = rule.originMorph.name || 'World';
            dropDownListItems.push({
                string: rule.selectorText() +
                '  (defined in ' + morphName + ')',
                rule: rule});
        });



    dropDownList.updateList(dropDownListItems);
}

