function displayRule(rule) {
    var propertiesPanel = this.get('PropertiesPanel'),
        declarations = rule.rule.declarations;
    propertiesPanel.removeAllMorphs();
    declarations.each(function(decl){
        this.insertPropertyField(decl, rule.rule.allDeclarations);
        }, this);
    this.displayedRule = rule;
    this.sortPanels();
}

