function markOverriddenProperties(rules) {
    /*
    Iterates over the list of rules (which are ordered from low to 
    high specificity) and marks all overridden declarations.
    Returns the list of rules.
    */
    for (var i = rules.length - 1; i >= 0; i--) {
        var rule = rules[i];
        rule.declarations.select(function(decl){
                return !decl.overriddenBy;
            }).each(function(decl){
                for (var j = i - 1; j >= 0; j--) {
                    rules[j].declarations.each(function(d) {
                            if (d.property === decl.property) {
                                d.overriddenBy = rule;
                            }
                        });
                }
            });
    }
    return rules;
}

