function sortPanels() {
    var panels = this.get('PropertiesPanel').submorphs,
        topCounter = 0,
        spacing = 10;
    if (panels) {
        panels.sort(function(a,b) {
                return a.get('PropertyNameDisplay').textString >
                    b.get('PropertyNameDisplay').textString;
            }).each(function(panel) {
                panel.setPosition(pt(panel.getPosition.x, topCounter));
                topCounter += spacing + panel.getExtent().y;
            });

    }
}

