function setRules(target) {
    // rules are ordered already, right?
    var dropDownListItems = [],
        dropDownList = this.get('RuleList'),
        rules = target.getMatchingStyleSheetRules(),
        allDeclarations = target.getMatchingStyleSheetDeclarations(),
        overrideList = target.generateStyleSheetDeclarationOverrideList(allDeclarations),
        declarationsAndOverriders = [];

    for (var i = 0; i < allDeclarations.length; i++) {
        var overriddenByIndex = overrideList[i],
            overriddenBy = overriddenByIndex >= 0 ? allDeclarations[overriddenByIndex] : null;
        declarationsAndOverriders.push(
                {
                    declaration: allDeclarations[i],
                    overriddenBy: overriddenBy
                }
            );
    }

    dropDownListItems.push({
            string: '*All active matching declarations*',
            allDeclarations: true,
            declarations: declarationsAndOverriders
        });

    rules.reverse().each(function(rule){
            var morphName = rule.getOriginMorph().getName()|| 'World';
            dropDownListItems.push({
                string: rule.getSelector()
                    + '  (defined in '
                    + (rule.getStyleSheet().isBaseTheme ? 'Base Theme' : morphName)
                    + ')',
                declarations: declarationsAndOverriders.select(
                        function(decl) {
                            return decl.declaration.getRule() === rule;
                        }
                    )
                });
        });



    dropDownList.updateList(dropDownListItems);
}

