function markOverriddenProperties(rules) {
    /*
    Iterates over the list of rules (which are ordered from low to 
    high specificity) and marks all overridden declarations.
    Returns the list of rules.
    */
    for (var i = rules.length - 1; i >= 0; i--) {
        var rule = rules[i];
        rule.getDeclarations().select(function(decl){
                return !decl.overriddenBy;
            }).each(function(decl){
                for (var j = i - 1; j >= 0; j--) {
                    rules[j].getDeclarations().each(function(d) {
                            if (d.getProperty() === decl.getProperty()) {
                                // FIXME: this stops working at some point ...
                                d.overriddenBy = rule;
                            }
                        });
                }
            });
    }
    return rules;
}

