function insertPropertyField(decl, allDeclarations, overriddenBy) {
    /*
    Creates a property editing field in the style browser.

    Property (String): The name of the CSS property to edit
    Values (Array): The property's values the panel is initialized with
    Active (Bool): Is the property active for the inspected morph?
    */
    var property = decl.getProperty(),
        values = decl.getValues(),
        //overriddenBy = decl.overriddenBy, // FIXMEE!!
        backPanel = this.get('PropertiesPanel'),

        cssProp = apps.cssParser.getPropList()[property.trim().toLowerCase()],
        valueTypes = this.getValueTypes(cssProp, values),
        valueCount = valueTypes.length,

        // the panel that is created
        panelSpacing = 10,
        panelHeight = 40,
        allDeclExtraHeight = allDeclarations ? 15 : 0,
        overrideExtraHeight = overriddenBy ? 15 : 0,
        actualPanelHeight = panelHeight * (valueCount + 1)
            + allDeclExtraHeight + overrideExtraHeight,
        collapsedHeight = panelHeight + allDeclExtraHeight + overrideExtraHeight,
        scrollbarWidth = 20,
        panelWidth = backPanel.getExtent().x - scrollbarWidth,
        widgetHeight = 20,
        widgetTop = Math.round(panelHeight / 2.5 - widgetHeight / 2.5),

        allDeclRuleOriginTop = widgetTop + widgetHeight,
        leftMargin = 10,
        rightMargin = 10,
        labelMargin = 10,
        panelTop = 0,
        panelStyle = {clipMode: 'hidden', fill: Color.fromString('#ddd'),
            borderWidth: 0, resizeWidth: true, adjustForNewBounds: true},
        panel = lively.morphic.Morph.makeRectangle(
            0,panelTop, panelWidth, collapsedHeight),
        // text morph on the panel that shows the prop name
        propertyNameDisplayStyle = {fill: null, fontSize: 10, borderWidth: 0},
        propertyNameDisplay = new lively.morphic.Text(
            new Rectangle(labelMargin, widgetTop, panelWidth ,widgetHeight));

/*
    if (!cssProp) {
        alert('do not have property '+property.trim().toLowerCase()+ ' in list ...');
        return;
    }
*/
    panel.applyStyle(panelStyle );
    panel.declaration = decl;
    panel.collapsedHeight = collapsedHeight;
    panel.normalHeight = actualPanelHeight;

    // For sorting ...
    panel.sortValue = property;

    propertyNameDisplay.textString = property;
    propertyNameDisplay.applyStyle(propertyNameDisplayStyle);

    // If the "all declarations" option is displayed, 
    // panels also show their origin rules. If the declaration
    // is overridden, the override rule is shown
    if (allDeclarations) {
        var ruleOriginDisplay = new lively.morphic.Text(
                new Rectangle(labelMargin,
                allDeclRuleOriginTop, panelWidth - labelMargin, widgetHeight)),
            ruleOriginDisplayStyle = {fill: null, fontSize: 8, borderWidth: 0};

        allDeclRuleOriginTop += allDeclExtraHeight;
        ruleOriginDisplay.applyStyle(ruleOriginDisplayStyle);
        ruleOriginDisplay.setName('RuleOriginDisplay');
        panel.addMorph(ruleOriginDisplay);

        ruleOriginDisplay.textString = 'defined in ' + decl.getRule().getSelector();
    }

    // If the "all declarations" option is displayed, 
    // panels also show their origin rules. If the declaration
    // is overridden, the override rule is shown
    if (overriddenBy) {
        var ruleOriginDisplay = new lively.morphic.Text(
                new Rectangle(labelMargin,
                allDeclRuleOriginTop, panelWidth - labelMargin, widgetHeight)),
            ruleOriginDisplayStyle = {fill: null, fontSize: 8, borderWidth: 0};


        ruleOriginDisplay.applyStyle(ruleOriginDisplayStyle);
        ruleOriginDisplay.setName('RuleOriginDisplay');
        panel.addMorph(ruleOriginDisplay);

        propertyNameDisplay.applyStyle({textDecoration: 'line-through'});
        ruleOriginDisplay.textString = 'overridden by '
            + overriddenBy.getRule().getSelector();
    }

    // Add the label
    propertyNameDisplay.setName('PropertyNameDisplay');
    panel.addMorph(propertyNameDisplay);

    // Add the delete button
/*
    panel.addMorph(this.createDeleteButton(
        new Rectangle(panelWidth - rightMargin + 2 * labelMargin ,
        widgetTop, widgetHeight, widgetHeight), decl));
*/

    // Add the collapse button
    panel.addMorph(this.createCollapseButton(
        new Rectangle(panelWidth - rightMargin - widgetHeight ,
        widgetTop, widgetHeight, widgetHeight)))
        // The label is not aligned properly when we don't set the extent here ... why?
        .setExtent(pt(widgetHeight, widgetHeight));

    // Add the panels for all values
    for (var i = 0; i < valueCount; i++) {
        var valuePanel = this.makeValuePanel(
                valueTypes[i], i, panelWidth - leftMargin - rightMargin, panelHeight);
        panel.addMorph(valuePanel);
        valuePanel.setPosition(pt(leftMargin, collapsedHeight + i * panelHeight));
        if (valuePanel.setValue) {
            valuePanel.setValue(values[i]);
        } else {
            alert(property+': There is a setValue implementation missing ...');
        }
    }

    // If there are no values, just create a default/text value panel
    if (valueCount === 0) {
        var valuePanel = this.makeValuePanel(
                0, panelWidth - leftMargin - rightMargin, panelHeight);
        panel.addMorph(valuePanel);
        valuePanel.setPosition(pt(leftMargin, 0));
        valuePanel.setValue(decl.getValues().reduce(function(prev, val, i, a){
                return prev + (prev.length > 0 ? ' ' : '') + val;
            }, ''));
    }

    backPanel.addMorph(panel);

}

