function getValueTypes(cssProp, values) {
    var valueCount = values.length,
        valueTypes = [];

    if (cssProp) {
        var valueConfig = cssProp.values.find(function(val) {
                return val.length === valueCount;
            });

        if (valueConfig) {
            valueTypes = valueConfig;
        } else {
            var v = valueCount,
                altValueConfig;
            while (v > 0 && !(altValueConfig = cssProp.values.find(function(val) {
                    return val.length === v;
                }))) {
                v--;
            }
            if (v === 0) {
                alert('Woops! The property cannot have '+valueCount+' values :(');
                return [];
            } else {
                valueTypes = altValueConfig;
            }
        }
        return valueTypes;
    } else {
        return [0]
    }
}

