function createDeleteButton(bounds, decl) {
    var deleteButton = new lively.morphic.Button(bounds, 'X');
    deleteButton.decl = decl;
    deleteButton.addScript(function doAction(){
            var rule = this.decl.getRule(),
                browser = this.get('StyleEditorPane');
            rule.declarations = rule.getDeclarations().select(function(d){
                    return d != this.decl;
                }, this);
            browser.declarationHasChanged(this.decl);
            browser.displayRule(browser.displayedRule);
        });
    connect(deleteButton, 'fire', deleteButton, 'doAction');
    return deleteButton;
}

