function createCollapseButton(bounds) {
    var collapseButton = new lively.morphic.Button(bounds, 'v');
    collapseButton.collapsed = true;

    collapseButton.addScript(function doAction(){
            var panel = this.owner;
            this.collapsed = !this.collapsed;
            if (this.collapsed) {
                this.setLabel('v');
                panel.setExtent(panel.getExtent().withY(panel.collapsedHeight));
            } else {
                this.setLabel('^');
                panel.setExtent(panel.getExtent().withY(panel.normalHeight));
            }
            this.get('StyleEditorPane').sortPanels();
        });

    connect(collapseButton, 'fire', collapseButton, 'doAction');
    return collapseButton;
}

