function init() {
    module('lively.LayerableMorphs').load(true);
    var textAttributePanel = this;
    this.getObserveTextLayer().refineClass(lively.morphic.Text, {
        onFocus: function(evt) { cop.proceed(evt); textAttributePanel.focusedText = this },
        onBlur: function(evt) { cop.proceed(evt); /*textAttributePanel.focusedText = null*/ },
    }).beGlobal()
}

863.source:
function getObserveTextLayer() {
    if (this.layer) return this.layer;
    var layerName = 'ObserveTextFocusLayer', counter = 1;
    while (Global[layerName + counter]) { counter++ }
    layerName += counter;
    this.layer = cop.create(layerName);
    this.setWithoutLayers([this.layer]);
    return this.layer;
}

