function onBoldButtonClicked(value) {
    this.checkSelection();
    var emphRange = this.getSelectionRange();

    this.focusedText.changeEmphasis(emphRange[0], emphRange[1], function(emph, doEmph) {
        doEmph({fontWeight: (value ? 'bold' : 'normal')});
    });
}

