function runCode() {
    // this.runCode()
    // This is connected to the interface Run button, and is also invoked if the user saves new code in the code pane.
    var self = this;
    function whenReady() {
        var shinyStarted = false;
        if (window.Shiny) {
            self.haltShinyApp();
            delay = 250;
        } else { delay = 0 };
        setTimeout(function() {
            self.runRCode(function(err, results) {
                // console.log(Date.now());
                // long-lived function receiving results from R
                if (results) {
                    results.forEach(function(res) {
                        // console.log(res)
                        // Wait for R to print Shiny's "Listening" message before initialising Shiny at this end.
                        if (res.message) {
                            if (!shinyStarted && res.message.indexOf("Listening on port") !== -1) {
                                shinyStarted = true;
                                var portNumber = Number(res.message.trim().split(" ").last());
                                console.log(portNumber)
                                self.startShiny(portNumber);
                            } else { console.log(res.message) }
                        }
                        if (res.warning) console.log("A warning: ", res.warning);
                    })
                }
                // if R reports an error, immediately halt the Shiny app
                if (err) {
                    show(err);
                    self.haltShinyApp();
                }
            });
        }, delay);
    };

    this.ensureRAndLibs(function(err, res) {
        if (!err) whenReady()
        });
}

