function haltShinyApp() {
    // this.haltShinyApp()
    // This is connected to the interface's Stop button, and is intended to undo the effects of the Run button (e.g., prior to a new run).  Namely:
    //   1. Stop evaluation of any R ggvis thread
    //   2. Detach Shiny bindings from elements on the page
    // It is also invoked if a running R/ggvis session hits an R error.
    if (this.RevalID) {
        apps.RInterface.livelyREvaluate_stopEval(this.RevalID, function(error, result) { if (error) show(error); else alertOK("old R eval stopped") });
        this.RevalID = null
    }
    // unbindAll only appears once Shiny has been initialised
    if (window.Shiny && window.Shiny.unbindAll) window.Shiny.unbindAll();
}

