function loadVegaAndShiny(thenDo) {
    // this.loadVegaAndShiny(function(err,res) { show("done") })
    // load vega, and then shiny and shiny-ggvis without starting either
    // This should be ok to run multiple times on a page: shiny.js will
    // just replace any previously set up window.Shiny.
    Global.deferredShinyInit = true;
    Global.shinyGgvisInitialized = false;

    var serverURL = Global.document.location.host;
    [
    // 1. load d3
    function(next) {
        require("apps.d3Interface").toRun(function() {

        // 2. load vega
            var url = "http://" + serverURL + "/R-libraries/ggvis/www/lib/vega.js";
            new WebResource(url).whenDone(function(src, status) {
                if (!status.isSuccess()) {
                    show('cannot load vega! %s', status);
                } else {
                    try { eval(src); next() } catch (e) {
                        show('cannot load vega! %s', e.stack || e); }
                }
            }).beAsync().get();
        })
    },

    // 3. load shiny (and change the websocket url)
    // Function initShiny ends up sending a message
    // to the server announcing that the page is ready to receive chart messages.
    function(next) {
        var url = "http://" + serverURL + "/R-libraries/shiny/www/shared/shiny.js";
        new WebResource(url).whenDone(function(src, status) {
            if (!status.isSuccess()) {
                show('cannot load shiny! %s', status);
            } else {
                // hack the socket definition to pick up the URL we build
                src = src.replace(/new WebSocket.*/, 'new WebSocket(Strings.format("ws://%s/", Global.shinyServerURL),"shiny");');
                // (earlier-binding version) src = src.replace(/new WebSocket.*/, Strings.format('new WebSocket("ws://%s/", "shiny");', shinyServerURL));
                try { eval(src); console.log("Loaded shiny"); next(); } catch (e) {
                    show('cannot load shiny! %s', e.stack || e); }
            }
        }).beAsync().get();
    },
    // 4. Load shiny-ggvis. This sets up the methods for handling messages from
    // the server.
    // In the current setup, the DOM entry for 'ShinyGigvisMorph' is set up with the id
    // 'plot1', hard-coded into gigvis on the R side as the chart id.
    function(next) {
        var url = "http://" + serverURL + "/R-libraries/ggvis/www/js/shiny-ggvis.js";
        new WebResource(url).whenDone(function(src, status) {
            if (!status.isSuccess()) {
                show('cannot load shiny-ggvis! %s', status);
            } else {
                try { eval(src); console.log("Loaded shiny-ggvis"); next(); } catch (e) {
                    show('cannot load shiny-ggvis! %s', e.stack || e); }
            }
        }).beAsync().get();
    }
    ].doAndContinue(null, function() {
        alertOK("Shiny, ggvis, Vega loaded");
        thenDo(null, "ok");
        });
}

