function ensureR(thenDo, forceReload) {
    // this.ensureR(function(err,res) {}, true)  // to force reload
    // Make sure the interface to R is running, and the top-level R process has [the latest versions of] the packages we need.
    var self = this;
    require('apps.RInterface').toRun(function() {
        function initAndDo(thenDo) {
            apps.RInterface.evalSync('if (exists("ggvis")) detach(name="package:ggvis",unload=TRUE)\n if (exists("reactive")) detach(name="package:shiny",unload=TRUE)\n library(shiny)\n library(ggvis)\n options(shiny.withlively=TRUE)\n', function(err, result) {
            if (err) show(err); else alertOK("R libraries (re)loaded");
            thenDo(err, result);
            })
        }

        if (forceReload) {
            initAndDo(thenDo);
        } else {
            self.isRReady(function(rReady) {
                if (rReady) thenDo(null, "R ready"); else initAndDo(thenDo);
            });
        }
    })
}

