function refreshServerList() {
    var serverList = this.get('serverList'),
        master = this.getMasterServerInterface(),
        prevSel = serverList.selection;
    this.reset();
    connect(
        master, 'serverInfos',
        {$: function(list) {
            var items = list.collect(function(ea) {
                return {string: ea.serverName, value: ea, isListItem: true}
            });
            serverList.setList(items);
            if (prevSel && prevSel.serverName) 
                serverList.
setSelectionMatching(prevSel.serverName);
        }}, '$')
    master.getServerInfos();
}

