function getMasterServerInterface() {
    var master = new NodeJSMasterServer()
    master.beAsync();
    return master;
}

422.source:
function showServerInfo() {
    if (!this.currentServer) return;
    if (this.currentServer.isRunning) this.serverIsRunning();
    else this.serverIsNotRunning();
    // var info = JSON.prettyPrint(this.currentServer);
    // this.get('serverInfoText').textString = info;

    var master = this.getMasterServerInterface();
    master.isAsync = false;

    var url = 'http://lively-kernel.org/nodejs/' + this.currentServer.serverName + '/';
    this.get('urlText').textString = url;
    this.get('urlText').emphasizeAll({uri: url});

    this.get('logText').textString = '';
    this.get('logText').textString = master.getLog(this.currentServer.serverName)
}

