function restartCurrentServer() {
    if (!this.currentServer) return;

    var master= this.getMasterServerInterface(),
        serverName = this.currentServer.serverName;

    connect(master, 'lastServerResult', this, 'refreshServerList');

    master.updateAndRestart(serverName);
}

