function removeServer() {
    var serverName = this.currentServer.serverName;
    this.world().confirm(
        'Really remove ' + serverName + '?' ,
        function(input) {
            if (!input) { alertOK('nothing removed'); return };
            ServerCreator.remove(serverName);
            this.refreshServerList();
        }.bind(this))
}

