function makePieces() {
    this.submorphs.invoke('remove');    
    this.owner.flipper = this.index

    this.flipMap = [
        [0, 1, 3],		[0, 1, 2, 4],		[1, 2, 5],
		[0, 3, 4, 6],	[1, 3, 4, 5, 7],	[2, 4, 5, 8],
		[3, 6, 7],		[4, 6, 7, 8],		[5, 7, 8]]

    for (i = 0; i < 3; i++) {
        for (j = 0; j < 3; j++) {
            var box = lively.morphic.Morph.makeCircle(pt(100, 100), 50)
            box.setPosition(pt(j * 100 + 50, i * 100 + 50))
            box.head = Math.random() > 0.5
            box.disableGrabbing()
            box.setFill(box.head ? Color.green : Color.red)
            box.index = i * 3 + j
            box.addScript(function onMouseDown(evt) {
                if (this.owner.scripts.length > 0)
                    return true
                this.owner.flipStartTime = Date.now()
                this.owner.flipState = "up"
                this.owner.flipper = this.index
                this.owner.startStepping(20, "flip")
                return true
            });
            this.addMorph(box)
        }
    }
    if (this.yay)
        this.yay.remove()
    var nowCount= this.submorphs.pluck('head').select(Functions.K).length
    if (nowCount === 0 || nowCount === 9)
        this.makePieces()
    this.trials = 0
    this.updateLabels()
}

