function flip() {
    var now = Date.now();
    var elapsed = now - this.flipStartTime
    if (!Object.isNumber(this.flipper))
        return;
    this.forEachIn(this.flipper, function(morph, ind) {
        morph.setScale(this.scaler(elapsed))
    }.bind(this))
    if (this.flipState == "up" && elapsed >= 500) {
        this.flipState = "down"
        this.forEachIn(this.flipper, function(morph, ind) {
            morph.setFill(morph.head ? Color.red : Color.green)
            morph.head = !morph.head
        })
    }
    if (this.flipState == "down" && elapsed >= 1000) {
        this.flipper = null
        this.flipState = null
        this.stopStepping()
        this.addCount()
        this.updateLabels()
        return;
    }
}

