function updateLabels() {
    var count = this.submorphs.pluck('head').select(Functions.K).length
    this.get("greens").textString = "green: " + count
    this.get("trials").textString = "trials: " + this.trials
    if (count == 9) {
        if (this.yay)
            this.yay.remove()
        this.yay = this.makeYay()
        this.owner.addMorphFront(this.yay)
    }
}

